mapper.select:
select notice_brd_seq, notice_brd_title, notice_brd_content, notice_brd_regdate, notice_brd_hit
  from NoticeBoard
 where notice_brd_seq=#{notice_brd_seq}

mapper.update:
update NoticeBoard
   set notice_brd_title=#{notice_brd_title}, notice_brd_content=#{notice_brd_content}
 where notice_brd_seq=#{notice_brd_seq}
