mapper.select:
select routine_brd_seq, routine_brd_title, routine_brd_content, routine_brd_regdate, routine_cart_seq, brd_image1, brd_image2
  from RoutineBoard
 where routine_brd_seq=#{routine_brd_seq}

mapper.update:
update RoutineBoard
   set routine_brd_seq=#{routine_brd_seq}, routine_brd_title=#{routine_brd_title}, routine_brd_content=#{routine_brd_content}
 where routine_brd_seq=#{routine_brd_seq}
