mapper.select:
select routine_brd_seq, routine_brd_title, routine_brd_content, routine_brd_regdate, routine_cart_seq, brd_image1, brd_image2
  from RoutineBoard
 where routine_brd_seq=#{routine_brd_seq}

mapper.update:
update RoutineBoardComment
set routine_cmt_ref = #{routine_cmt_seq}
where routine_cmt_seq = #{routine_cmt_seq}
