mapper.update:
update User
set user_age = #{user_age}, user_height=#{user_height}, user_weight=#{user_weight}, user_img=#{user_img}
where user_email = #{user_email}

update Role
set role_name='ROLE_REGULAR_USER'
where user_email = #{user_email}

update User
set enabled=0
where user_email=#{user_email}

mapper.select:
select count(*)
from User
where user_email=#{user_email}
